program main
 use mpmodule
 use ttmp_lib
 !use mpblas_lib
 use omp_lib
 use mp_dmrgg_lib
 use time_lib
 implicit none
 include "mpif.h"
 type(mptt) :: qq,tt
 integer :: i,j,p,m,n,nx,r,piv,decay,info,nproc,me,adj,saydigits
 integer(kind=8) :: neval
 double precision :: t1,t2,tcrs,acc
 type(mp_real),allocatable::par(:)
 integer,parameter :: ndp1=mpipl, nwds1=int(ndp1/mpdpw+2)
 type(mp_real) :: zero,one,two,five,seven,ten,tpi,log2,half,zeta3
 type(mp_real) :: f,t, tru,val
 type(mp_real),external :: mpfunc_ising_discr
 logical :: rescale
 character(len=1) :: a
 character(len=32) :: aa
 character(len=2048) :: stmp
 
 zero="0.d0"; one="1.d0"; two="2.d0"; five="5.d0"; seven="7.d0"; ten="10.d0"; half="0.5d0"
 tpi=2*mppi(nwds1); log2=mplog2(nwds1)
 acc=-100.d0
 saydigits=int(-acc)

 ! Read params
 call getarg(1,aa); read(aa,'(a1)')a  ; if (a.eq.' ')a='c'      ! dimension of the problem
 call getarg(2,aa); read(aa,'(i10)') m; if (m.eq.0)  m=3        ! dimension of the problem
 call getarg(3,aa); read(aa,'(i10)') n; if (n.eq.0)  n=65       ! stoch. mode size
 call getarg(4,aa); read(aa,'(i10)') r; if (r.eq.0)  r=30       ! max rank
 call getarg(5,aa); read(aa,'(i10)') piv;  ! pivoting strategy

 call mpi_init(info)
 if(info.ne.0)then;write(*,*)'mpi: init fail: ',info;stop;endif
 call mpi_comm_size(MPI_COMM_WORLD,nproc,info)
 if(info.ne.0)then;write(*,*)'mpi: comm_size fail: ',info;stop;endif
 call mpi_comm_rank(MPI_COMM_WORLD,me,info)
 if(info.ne.0)then;write(*,*)'mpi: comm_rank fail: ',info;stop;endif
 !write(*,'(a,i3,a,i3)')'mpi: I am ',me,' of ',nproc
 
 adj=0
 if(mod(n,2).eq.0)then;n=n+1;adj=1;endif
 if(me.eq.0)then
  write(*,'(a)') 'Hi, this is TT cross interpolation computing Ising integral...'
  write(*,'(3x,a,a10)') 'integral :',a
  write(*,'(3x,a,i10)') 'dimension:',m
  if(adj.eq.0)then
   write(*,'(3x,a,i10)') 'quadratur:',n
  else 
   write(*,'(3x,a,i10,a)') 'quadratur:',n,' (adjusted)'
  end if 
  write(*,'(3x,a,i10)') 'TT ranks :',r
  write(*,'(3x,a,i10)') 'pivoting :',piv
  write(*,'(3x,a,i10)') 'MPI procs:',nproc
!$OMP PARALLEL
  if (omp_get_thread_num().eq.0) then
   write(*,'(3x,a,i10)')'OMP thrds:', omp_get_num_threads()
  end if 
!$OMP END PARALLEL
  write(*,'(3x,a,i10)') 'MPF digts:',mpipl
  write(*,'(3x,a,i10)') 'MPF wds6 :',mpwds6
 end if

 allocate(par(2*n+1), stat=info)
 if(info.ne.0)then;write(*,*)'cannot allocate par:',info;stop;endif
 if(a.eq.'c' .or. a.eq.'C')then;par(2*n+1)="1"
 elseif(a.eq.'d' .or. a.eq.'D')then;par(2*n+1)="2"
 elseif(a.eq.'e' .or. a.eq.'E')then;par(2*n+1)="3"
 else;write(*,*)'unknown integral type:',a;stop
 endif
 ! benchmark data from http://crd-legacy.lbl.gov/~dhbailey/dhbpapers/ising-data.pdf
 tru=zero
 zeta3="1."
 if(a.eq.'c' .or. a.eq.'C')then
  if(m.eq.2)tru="1.d0"
  if(m.eq.3)tru="0."
  if(m.eq.4)tru="0."
  !if(m.eq.4)tru=7.d0*zeta3/12
  if(m.eq.5)tru="0."
  if(m.eq.6)tru="0."
  if(m.eq.8)tru="0."
  if(m.eq.16)tru="0."
  if(m.eq.32)tru="0."
  if(m.eq.64)tru="0."
  if(m.eq.128)tru="0."
  if(m.eq.256)tru="0."
  if(m.eq.512)tru="0."
  if(m.eq.1024)tru="0."
 elseif(a.eq.'d' .or. a.eq.'D')then
  if(m.eq.2)tru=one/3
  if(m.eq.3)then;tru="0.";tru=8+tpi**2/3-27*tru;endif
  if(m.eq.4)tru=tpi**2/9 - one/6 - seven*zeta3/2
  if(m.eq.5)tru="0."
  if(m.eq.6)tru="0.00048914170018803477510066231535045603322055262753059988378760460832244913947351750130777133802299560444551d0"
 elseif(a.eq.'e' .or. a.eq.'E')then
  if(m.eq.2)tru=6-8*log2
  if(m.eq.3)tru=10-tpi**2/2-8*log2+32*log2**2
  !if(m.eq.4)tru=22.d0-24.d0*log2+176.d0*log2**2-256.d0*log2**3/3+4.d0*(tpi**2)*log2-11.d0*tpi**2/6.d0-(82.d0*12.d0/7.d0)*0.
  if(m.eq.4)tru=22-82*zeta3-24*log2+176*log2**2-256*log2**3/3+4*(tpi**2)*log2-11*tpi**2/6
  if(m.eq.5)tru="0.00349365371172952174068806727918425156963294495514131468369898233699924152717266576695087067520893264332903998566861235384768599443866815487779823641439966119140137365416727476965866845233975094131294703225222116183255112718650890146021418d0"
  if(m.eq.6)tru="0.0006878328718264094370047842736902107038148033103222727175338965396792103931668620590718645325543697533105467758387352231831720375645991880602098222503718205681784822803225117868730347366955186275157082427875765461445655735856457109451244033162090681436511005147862501959090d0"
 endif 
 
 call mp_lgwt(n,par(1),par(n+1))         ! legendre gauss quadrature on [-1,1]
 call mpi_barrier(mpi_comm_world)
 if(me.eq.0)write(*,'(3x,a,a)')'quadrature: ', 'Legendre-Gauss'
 !do i=1,n
 ! write(*,'(e50.40,1x,e50.40)') dble(par(i)), dble(par(n+i))
 !end do
 do i=1,n
  par(n+i)=par(n+i)/2                      ! rescale weights make it a measure
  par(i)=(par(i)+1)/2                      ! translate nodes [-1,1] -> [0,1]
 end do
 
 qq%l=1;qq%m=m-1;qq%n=n;qq%r=1;call alloc(qq);
 do i=1,m-1;call mpcopy(n,par(n+1),1,qq%u(i)%p,1);enddo
 call ones(qq)
 tt%l=1;tt%m=m-1;tt%n=n;tt%r=1;call alloc(tt)
 
 ! RESCALE to avoid underflow
 rescale=(a.eq.'d' .or. a.eq.'D' .or. a.eq.'e' .or. a.eq.'E') .and. (m.ge.10)
 if(rescale)then
  val=n*half
  call mpscal(n,five*val,par(n+1),1)
  do i=1,m-1;qq%u(i)%p=one/val;end do
 else 
  val=n*half
  call mpscal(n,val,par(n+1),1)
  do i=1,m-1;qq%u(i)%p=one/val;end do
 end if
 
 t1=timef()
 if(tru.eq.0.d0)then
  call mptt_dmrgg(tt,mpfunc_ising_discr,par,maxrank=r,accuracy=acc,pivoting=piv,neval=neval,quad=qq)
 else 
  call mptt_dmrgg(tt,mpfunc_ising_discr,par,maxrank=r,accuracy=acc,pivoting=piv,neval=neval,quad=qq,tru=tru)
 endif
 t2=timef()
 tcrs=t2-t1
 if(me.eq.0)write(*,'(a,i12,a,e12.4,a)') '...with',neval,' evaluations completed in ',tcrs,' sec.'

 val = mptt_quad(tt,qq)
 if(me.eq.0) then
  write(stmp,'(a)')
  call mpsay(val, mpipl+20, saydigits, stmp)
  if(rescale)then
   write(*,'(2a,a,i4,a)') 'computed value:', trim(stmp),' / (5**',m-1,')'
  else
   write(*,'(2a)') 'computed value:', trim(stmp)
  end if
  if(tru.ne.zero)then
   call mpsay(tru, mpipl+20, saydigits, stmp)
   write(*,'(2a)')     'analytic value:',trim(stmp)
   write(*,'(a,f9.2)') 'correct digits:', -dble(log(abs(1-val/tru))/log(ten))
  end if
  write(*,'(a)')'Good bye.'
 endif
 call dealloc(tt)
 call mpi_finalize(info)
 if(info.ne.0)then;write(*,*)'mpi: finalize fail: ',info;stop;endif
end program

type(mp_real) function mpfunc_ising_discr(m,ind,n,par) result(f)
 use mpmodule
 implicit none
 integer,intent(in) :: m
 integer,intent(in) :: ind(m),n(m)
 type(mp_real),optional :: par(*)
 integer :: nodes,weights,i,j,id
 type(mp_real) :: uij,a,b,v,w,wk,vk,one
 one="1.d0"
 id=int(dble(par(2*n(1)+1)))
 nodes=0         ! t2 t3 ... tm
 weights=n(1)    ! w2 w3 ... wm
 if(id.eq.2 .or. id.eq.3)then
  a=one
  do i=0,m
   uij=one
   do j=i+1,m
    uij=uij*par(nodes+ind(j))
    a = a * (( uij-1 ) / ( uij+1 ))**2
   end do
  end do
 end if
 if(id.eq.1 .or. id.eq.2)then
  v=one; w=one; vk=one; wk=one
  do i=1,m
   vk=vk*par(nodes+ind(m-i+1))
   wk=wk*par(nodes+ind(i))
   v=v+vk
   w=w+wk
  end do
  b=one/(v*w)
 end if 
 select case(id)
 case(1);f=2*b
 case(2);f=2*a*b
 case(3);f=2*a
 case default
  write(*,*)'unknown id: ',id;stop
 end select 
  
 ! apply weights
 do i=1,m
  f=f*par(weights+ind(i))
 end do
end function
 
subroutine mp_lgwt(n, x, w)
  use mpmodule
  implicit none
  integer, intent(in) :: n
  type(mp_real) :: x(n), w(n)
  type(mp_real) :: small ! adaptive
  integer :: i, j, m
  type(mp_real) ::  p1, p2, p3, pp, z, z1, tpi,zero,one,two,ten
  integer, parameter :: ndp1=mpipl, nwds1=int(ndp1/mpdpw+2)
  zero="0.d0";one="1.d0";two="2.d0";ten="10.d0"
  small=ten**(-mpipl+1)
  tpi=2*mppi(nwds1)

  m = (n + 1) / 2
  do i = 1,m
    z = cos( (tpi * (4*i-1)) / (8*n+4) )
100 p1 = one
    p2 = zero
    do j = 1, n
    p3 = p2
    p2 = p1
    p1 = ((2*j-1) * z * p2 - (j-1)*p3) / j
    enddo

    pp = n*(z*p1-p2)/(z*z-1)
    z1 = z
    z = z1 - p1/pp             ! newton's method

    if (abs(z-z1) .gt. small) goto  100

      x(i)     =  -z
      w(i)     = two/((1-z*z)*pp*pp)
      if(2*i.ne.n+1)then
       x(n+1-i) =   z
       w(n+1-i) = w(i)
      endif
    end do     ! i loop
 end subroutine
