program main
 use time_lib
 use quad_lib
 use mat_lib
 use default_lib
 implicit none
 include "mpif.h"
 integer :: i,j,m,n,nx,q,r,piv,decay,info,nproc,me,neval
 double precision :: f,bnd,t1,t2,tcrs, einf,efro,ainf,afro, mem,logdet, val,tru, stddev
 double precision,allocatable::par(:)
 double precision,parameter :: tpi=6.
 double precision,parameter :: log2=0.
 double precision,parameter :: zeta3=1.20205690315959428539973816151144999076498629234049888179227155534183820578631309018645587360933525814619915d0
 character(len=1) :: a
 character(len=32) :: aa
 logical :: rescale
 double precision,external :: dfunc_ising_cont

 ! Read params
 call readarg(1,a,'c')      ! type of the integral, e.g. 'c', 'd' or 'e'
 call readarg(2,m,6)        ! index of the integral, e.g 6 to calculate C_6
 call readarg(3,q,22)       ! QMC level: the lattice has 2^q points
 call readarg(4,r,4)        ! QMC repeats r times to estimate the error

 call mpi_init(info)
 if(info.ne.0)then;write(*,*)'mpi: init fail: ',info;stop;endif
 call mpi_comm_size(MPI_COMM_WORLD,nproc,info)
 if(info.ne.0)then;write(*,*)'mpi: comm_size fail: ',info;stop;endif
 call mpi_comm_rank(MPI_COMM_WORLD,me,info)
 if(info.ne.0)then;write(*,*)'mpi: comm_rank fail: ',info;stop;endif
 !write(*,'(a,i3,a,i3)')'mpi: I am ',me,' of ',nproc
 
 if(me.eq.0)then
  write(*,'(a)') 'Hi, this is QMC computing Ising integral...'
  write(*,'(3x,a,a10)') 'integral   :',a
  write(*,'(3x,a,i10)') 'dimension  :',m
  write(*,'(3x,a,i10)') 'QMC level  :',q
  write(*,'(3x,a,i10)') 'QMC repeats:',r
  write(*,'(3x,a,i10)') 'MPI procs  :',nproc
!  if (omp_get_thread_num().eq.0) then
!   write(*,'(3x,a,i10)')'OMP threads:', omp_get_num_threads()
!  end if 
 end if

 allocate(par(2+2*m), stat=info)
 if(info.ne.0)then;write(*,*)'cannot allocate par:',info;stop;endif
 if(a.eq.'c' .or. a.eq.'C')then;par(1)=dble(1)
 elseif(a.eq.'d' .or. a.eq.'D')then;par(1)=dble(2)
 elseif(a.eq.'e' .or. a.eq.'E')then;par(1)=dble(3)
 else;write(*,*)'unknown integral type:',a;stop
 endif
 
 ! benchmark data from http://crd-legacy.lbl.gov/~dhbailey/dhbpapers/ising-data.pdf
 tru=0.d0
 if(a.eq.'c' .or. a.eq.'C')then
  if(m.eq.2)tru=1.d0
  if(m.eq.3)tru=0.
  !if(m.eq.4)tru=0.
  if(m.eq.4)tru=7.d0*zeta3/12
  if(m.eq.5)tru=0.
  if(m.eq.6)tru=0.
  if(m.eq.8)tru=0.
  if(m.eq.16)tru=0.
  if(m.eq.32)tru=0.
  if(m.eq.64)tru=0.
  if(m.eq.128)tru=0.
  if(m.eq.256)tru=0.
  if(m.eq.512)tru=0.
  if(m.eq.1024)tru=0.
 elseif(a.eq.'d' .or. a.eq.'D')then
  if(m.eq.2)tru=1.d0/3
  if(m.eq.3)tru=8.d0+tpi**2/3-27.d0*0.
  if(m.eq.4)tru=tpi**2/9.d0 -1.d0/6 - 7.d0*zeta3/2
  if(m.eq.5)tru=0.
  if(m.eq.6)tru=0.00048914170018803477510066231535045603322055262753059988378760460832244913947351750130777133802299560444551d0
 elseif(a.eq.'e' .or. a.eq.'E')then
  if(m.eq.2)tru=6.d0-8.d0*log2
  if(m.eq.3)tru=10.d0-tpi**2/2-8.d0*log2+32.d0*log2**2
  !if(m.eq.4)tru=22.d0-24.d0*log2+176.d0*log2**2-256.d0*log2**3/3+4.d0*(tpi**2)*log2-11.d0*tpi**2/6.d0-(82.d0*12.d0/7.d0)*0.
  if(m.eq.4)tru=22.d0-82.d0*zeta3-24.d0*log2+176.d0*log2**2-256.d0*log2**3/3+4.d0*(tpi**2)*log2-11.d0*tpi**2/6.d0
  if(m.eq.5)tru=0.00349365371172952174068806727918425156963294495514131468369898233699924152717266576695087067520893264332903998566861235384768599443866815487779823641439966119140137365416727476965866845233975094131294703225222116183255112718650890146021418d0
  if(m.eq.6)tru=0.0006878328718264094370047842736902107038148033103222727175338965396792103931668620590718645325543697533105467758387352231831720375645991880602098222503718205681784822803225117868730347366955186275157082427875765461445655735856457109451244033162090681436511005147862501959090d0
 endif 
 
 ! RESCALE to avoid underflow
 rescale=(a.eq.'d' .or. a.eq.'D' .or. a.eq.'e' .or. a.eq.'E') .and. (m.ge.10)

 t1=timef()
 call qmc(m-1,q,r,val, stddev,  dfunc_ising_cont, par)
 t2=timef()
 tcrs=t2-t1
 
 if(me.eq.0)write(*,'(a,i20,a,e12.4,a)') '...with ',(2_8**q)*r,' evaluations completed in ',tcrs,' sec.'
 if(me.eq.0) then
  if(rescale)then
   write(*,'(a,e30.22,a,i4,a)') 'computed value   :', val,' / (5**',m-1,')'
  else 
   write(*,'(a,e30.22)') 'computed value   :', val
  end if 
  write(*,'(a,e30.22)') 'standard deviation:', stddev
  if(tru.ne.0.d0)then
   write(*,'(a,e30.22)') 'analytic value   :', tru
   write(*,'(a,f7.2)')   'correct digits   :', -dlog(dabs(1.d0-val/tru))/dlog(10.d0)
  end if
  write(*,'(a)')'Good bye.'
 endif
 call mpi_finalize(info)
 if(info.ne.0)then;write(*,*)'mpi: finalize fail: ',info;stop;endif
end program

double precision function dfunc_ising_cont(m,y,par) result(f)
 ! y in [-1,1]^m, integral over [0,1]^m
 implicit none
 integer,intent(in) :: m
 double precision,intent(in) :: y(m)
 double precision,intent(inout),optional :: par(*)
 double precision,parameter :: tpi=6.28318530717958647692528676655900577d0
 integer :: i,j,id
 double precision :: uij,v,w,vk,wk,a,b
 id=int(par(1))
 if(id.eq.2 .or. id.eq.3)then
  a=1.d0
  do i=0,m
   uij=1.d0
   do j=i+1,m
    uij=uij*(y(j)+1.d0)/2
    a = a * (( uij-1.d0 ) / ( uij+1.d0 ))**2
   end do
   if(m+1.ge.10 .and. i.gt.0)a=a*5.d0
  end do
 end if
 if(id.eq.1 .or. id.eq.2)then
  v=1.d0; w=1.d0; vk=1.d0; wk=1.d0
  do i=1,m
   vk=vk*(y(m-i+1)+1.d0)/2
   wk=wk*(y(i)+1.d0)/2
   v=v+vk
   w=w+wk
  end do
  b=1.d0/(v*w)
 end if 
 select case(id)
 case(1);f=2*b
 case(2);f=2*a*b
 case(3);f=2*a
 case default
  write(*,*)'unknown id: ',id;stop
 end select 
end function
